var _=require("lodash"),chalk=require("chalk"),Paginator=module.exports=function(){this.pointer=0,this.lastIndex=0};Paginator.prototype.paginate=function(t,e,i){i=i||7;var n=Math.floor(i/2),a=t.split("\n");if(a.length<=i)return t;this.pointer<n&&this.lastIndex<e&&e-this.lastIndex<i&&(this.pointer=Math.min(n,this.pointer+e-this.lastIndex)),this.lastIndex=e;var r=_.flatten([a,a,a]),o=Math.max(0,e+a.length-this.pointer);return r.splice(o,i).join("\n")+"\n"+chalk.dim("(Move up and down to reveal more choices)")};